                           ) using(board_id) where BOARD_CATEGORY = #{BOARD_CATEGORY} and ${field} like '%'||#{text}||'%'
                             or ${field2} like '%'||#{text}||'%' order by BOARD_RE_REF desc, BOARD_RE_SEQ asc
                        ) b
                      ) where rnum >= #{start} and rnum <= #{end}

